mapper.select:
select

,

from comment
where NEWS_ID = #{newsId,jdbcType=INTEGER}
AND AN_ID = #{anId,jdbcType=INTEGER}

mapper.delete:
delete from comment
where NEWS_ID = #{newsId,jdbcType=INTEGER}
AND AN_ID = #{anId,jdbcType=INTEGER}

mapper.update:
update comment

    
        SUPPORT = #{support,jdbcType=INTEGER},
    
    
        TIME = #{time,jdbcType=TIMESTAMP},
    
    
        C_TEXT = #{cText,jdbcType=LONGVARCHAR},
    

where NEWS_ID = #{newsId,jdbcType=INTEGER}
AND AN_ID = #{anId,jdbcType=INTEGER}

update comment
set
SUPPORT = #{support,jdbcType=INTEGER},
TIME = #{time,jdbcType=TIMESTAMP},
C_TEXT = #{cText,jdbcType=LONGVARCHAR}
where NEWS_ID = #{newsId,jdbcType=INTEGER}
AND AN_ID = #{anId,jdbcType=INTEGER}

update comment
set
SUPPORT = #{support,jdbcType=INTEGER},
TIME = #{time,jdbcType=TIMESTAMP}
where NEWS_ID = #{newsId,jdbcType=INTEGER}
AND AN_ID = #{anId,jdbcType=INTEGER}
